examples.python:
import re

camel_regex = r"([a-z0-9])([A-Z])"

def camel_to_snake(text):
    return re.sub(camel_regex, r"\1_\2", text).lower()

print(camel_to_snake("myVariableName"))  # "my_variable_name"
print(camel_to_snake("userId"))  # "user_id"

